    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Connection conn = this.connect();
        PreparedStatement pstmt = null;
        ResultSet rs = null;
        String passText = new String(jPasswordField1.getPassword());
        try {
            pstmt = conn.prepareStatement("SELECT * FROM login WHERE loginID=? AND password=?");
            pstmt.setString(1, jTextField1.getText().toString());
            pstmt.setString(2, passText);
            rs = pstmt.executeQuery();
            boolean found = rs.next();
            if (found) {
                System.out.println("Login Successful!");
                new GUI2().setVisible(found);
                new GUI1().setVisible(!found);
                dispose();
            }
            else {
            JOptionPane.showMessageDialog(null,"Please enter correct credentials!!");
            }
        } catch (Exception e) {
            System.err.println(e.getMessage());
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

